import {Component} from 'component';

export class CollectionItem extends Component{
	constructor() {
		super(...arguments);

		this.refresh();
	}

	get defaults() {
		return {
			'data-eid': {
				value: '',
				type: String,
				sealed: true,
				refresh: true
			}
		};
	}

	template() {
		return '<content></content>'
	}

	refresh(){
		this.element.setAttribute('data-eid', this.dataEid);
	}
}