import {Component} from "component";
import * as utils from 'utils';

export class ExtendContainer extends Component {
	constructor(){
		super(...arguments);
		this.init();
	}

	init(){
		this.toogleHide(this.hide);
	}

	toogleHide(hide){
		this.element.classList[hide ? 'add' : 'remove']('hide');
	}

	get defaults(){
		return {
			"hide": {
				type: Boolean,
				value: false,
				refresh: true
			}
		}
	}

	refresh(attrName){
		switch(attrName){
			case 'hide':
				this.toogleHide(this.hide);
				break;
		}
	}

	template(){
		return `<content></content>`;
	}
}