import coImage from 'co-image';
import coText from 'co-text';
import {TextableComponent} from 'textable-component';

export class ExtendedCard extends TextableComponent {

	constructor() {
		super(...arguments);
	}

	get defaults() {
		return {
			title: {
				value: {
					"html": "<b>Text</b>"
				},
				scheme: coText.constructor.prototype.defaults,
				base: coText.constructor,
				type: Object,
				sealed: true
			},
			"imagemodel": {
				value: {
					"position": "center center",
					"size": "contain"
				},
				scheme: coImage.constructor.prototype.defaults,
				base: coImage.constructor,
				type: Object,
				sealed: true
			},
			text: {
				value: {
					"html": "<b>Text</b>"
				},
				scheme: coText.constructor.prototype.defaults,
				base: coText.constructor,
				type: Object,
				sealed: true
			}
		}
	}

	template() {
		return require('../template.html');
	};

	refresh(){}
}
