import { Component } from 'component';

export class OutputSlider extends Component {
	constructor() {
		super(...arguments);
	}

	get defaults() {
		return {
			min: {
				type: Number,
				value: 0,
				refresh: true
			},
			max: {
				type: Number,
				value: 100,
				refresh: true
			},
			step: {
				type: Number,
				value: 1,
				refresh: true
			},
			value: {
				type: Number,
				value: 0,
				refresh: true
			},
			fill: {
				type: Boolean,
				value: true,
				refresh: true,
				optional: true
			},
			reverse: {
				type: Boolean,
				value: false,
				refresh: true,
				optional: true
			},
			orientation: {
				type: String,
				value: 'horizontal',
				values: ['horizontal', 'vertical'],
				refresh: true
			},
			disabled: {
				type: Boolean,
				value: false,
				refresh: true
			}
		};
	}

	template() {
		return require('../template.html');
	}

	refresh() {

	}
}