import { Component } from 'component';
import coText from 'co-text';

export class TextButton extends Component {
	constructor() {
		super(...arguments);
	}

	get defaults() {
		return {
			toggles: {
				type: Boolean,
				value: false
			},
			active: {
				type: Boolean,
				value: false,
				refresh: true
			},
			pressed: {
				type: Boolean,
				value: false,
				refresh: true
			},
			shadow: {
				type: Boolean,
				value: false,
				refresh: true
			},
			disabled: {
				type: Boolean,
				value: false,
				refresh: true
			},
			text: {
				type: Object,
				value: {
					'html': 'Text'
				},
				base: coText.constructor,
				scheme: coText.constructor.prototype.defaults,
				sealed: true
			}
		}
	}

	template() {
		return require('../template.html');
	}

	refresh() {

	}
}
