import {TextableComponent} from 'textable-component';

export class Text extends TextableComponent{
	constructor(){
		super(...arguments);
	}

	get defaults(){
		return {
			html: {
				value: 'Text',
				type: Text,
				refresh: true
			}
		};
	}

	template(){
		return require('../template.html');
	}



	refresh(){}
}