class CobaltDispatcherApi{
	constructor(cobaltDispatcher){
		this.cobaltDispatcher = cobaltDispatcher;
	}

	get ActionType(){
		return this.cobaltDispatcher.ActionType;
	}

	get EventType(){
		return this.cobaltDispatcher.EventType;
	}

	dispatch(action){
		this.cobaltDispatcher.dispatchAction(action);
	}

	subscribe(cb){
		return this.cobaltDispatcher.subscribeEvent(cb);
	}
}

module.exports = CobaltDispatcherApi;