import {Event} from './Event';
import {mixin} from 'utils';

const isMouse = !!window.MouseEvent;

export class MouseEvent extends Event {

	constructor() {
		super(...arguments);
		this.isMouse = isMouse;
		this.numOfTouches = 1;
	}

	static get isMouse(){
		return isMouse;
	}

	get events() {
		return {
			start: 'mousedown', 
			move: 'mousemove', 
			end: 'mouseup'
		};
	}

	get options() {
		return mixin(super.options, {
			mouseEvent: this.event
		});
	}

	moveHandler(event){
		this.event = event;
		super.moveHandler(event);
	}

}
