export default (global => {
	let supportsPassiveOption = false;

	try {
		global.addEventListener('test', null, Object.defineProperty({}, 'passive', {
      		get: function() {
        		supportsPassiveOption = true;
      		}
   		}));
   		
		return supportsPassiveOption;
	} catch (e) {
		return supportsPassiveOption;
	}

})(window);