declare module 'utils' {
  export function template(templateString: string, data: any): string
  export function mixin(target: any, source: any, properties?: string[]): any
  export function deepMixin(target: any, source: any): any
  export function load(url: string, callback: () => void, async?: boolean): XMLHttpRequest
  export function toArray<Item>(collection: ArrayLike<Item>): Item[]
  export class Observer {
    subscribe(event: string, callback: (event: any) => void): void
    unsubscribe(event: string, callback: (event: any) => void): void
    publish(event: string, data?: any): void
    destroy(): void
  }
  export function dispatchEvent(target: HTMLElement, event: String, options?: Object): void
  export function rangeValue(value: number, min: number, max: number): number
}
